\begin{verbatim}
DirectTimeProblem(K, M, C, F, u0, t)
DirectTimeProblem(K, M, C, u0, t)
\end{verbatim}
Structure containing data for the time solver

\textbf{Constructor}

\begin{itemize}
\item \texttt{K::AbstractMatrix}: Stiffness matrix


\item \texttt{M::AbstractMatrix}: Mass matrix


\item \texttt{C:AbstractMatrix}: Damping matrix


\item \texttt{F::AbstractMatrix}: External force matrix


\item \texttt{u0::Tuple}: Initial conditions

\begin{itemize}
\item \texttt{u0[1]}: Initial displacement (or modal displacement)


\item \texttt{u0[2]}: Initial velocity (or modal velocity)

\end{itemize}

\item \texttt{t::AbstractVector}: Time points at which to evaluate the response

\end{itemize}
\textbf{Fields}

\begin{itemize}
\item \texttt{K}: Stiffness matrix


\item \texttt{M}: Mass matrix


\item \texttt{C}: Damping matrix


\item \texttt{F}: External force matrix


\item \texttt{u0}: Initial conditions

\begin{itemize}
\item \texttt{u0[1]}: Initial displacement


\item \texttt{u0[2]}: Initial velocity

\end{itemize}

\item \texttt{h::Real}: Time step

\end{itemize}
\textbf{Note}

\begin{itemize}
\item If \texttt{F} is not provided, it is assumed to be a zero matrix

\end{itemize}

\begin{verbatim}
Newmark(; γ0 = 0.5, β0 = 0.25)
\end{verbatim}
Newmark time solver

\begin{verbatim}
HHT(; γ0 = 0.5, β0 = 0.25, ρ = 1., αf = Inf)
\end{verbatim}
Hilber-Hughes-Taylor time solver

\begin{verbatim}
WBZ(; γ0 = 0.5, β0 = 0.25, ρ = 1., αm = Inf)
\end{verbatim}
Wood-Bossak-Zienkiewicz time solver

\begin{verbatim}
GeneralizedAlpha(; γ0 = 0.5, β0 = 0.25, ρ = 1., αf = Inf, αm = Inf)
\end{verbatim}
Generalized-α time solver

\begin{verbatim}
solve(prob::DirectTimeProblem, alg; progress = true)
\end{verbatim}
Direct time integration solver

\textbf{Inputs}

\begin{itemize}
\item \texttt{prob}: DirectTimeProblem structure


\item \texttt{alg}: Numerical integration algorithm

\begin{itemize}
\item \texttt{CentralDiff()}: Central difference


\item \texttt{RK4()}: Fourth-order Runge-Kutta


\item \texttt{Newmark()}: Newmark


\item \texttt{HHT()}: Hilber-Hughes-Taylor


\item \texttt{WBZ()}: Wood-Bossak-Zienkiewicz


\item \texttt{GeneralizedAlpha()}: Generalized-α (default)


\item \texttt{MidPoint()}: Mid-point rule

\end{itemize}

\item \texttt{progress}: Show progress bar

\end{itemize}
\textbf{Output}

\begin{itemize}
\item \texttt{sol}: Solution structure containing the response of the system at the given time points

\begin{itemize}
\item \texttt{u}: Displacement


\item \texttt{du}: Velocity


\item \texttt{ddu}: Acceleration

\end{itemize}
\end{itemize}

